sys_ui_policy.script_false:
function onCondition() {
	g_form.setDisplay('sc_category', true);
    g_form.setMandatory('sc_category', true);
	g_form.setReadOnly('sc_category', false);

	g_form.setDisplay('sc_item', false);
    g_form.setMandatory('sc_item', false);
	g_form.setReadOnly('sc_item', true);
	g_form.setValue('sc_item', '');
}

sys_ui_policy.script_true:
function onCondition() {
	g_form.setDisplay('sc_item', true);
    g_form.setMandatory('sc_item', true);
	g_form.setReadOnly('sc_item', false);

	g_form.setDisplay('sc_category', false);
    g_form.setMandatory('sc_category', false);
	g_form.setReadOnly('sc_category', true);
	g_form.setValue('sc_category', '');
}
